<?php
namespace Fwlib\Web;

use Fwlib\Util\UtilContainer;

/**
 * UrlGenerator Trait
 *
 * Should only work on http/https protocol.
 *
 * @see UrlGeneratorInterface
 *
 * @copyright   Copyright 2014-2015 Fwolf
 * @license     http://www.gnu.org/licenses/lgpl.html LGPL-3.0+
 */
trait UrlGeneratorTrait
{
    /**
     * Get parameters
     *
     * Initial value is HTTP GET, read through {@see HttpUtil::getGets()}.
     *
     * @type    string[]
     */
    protected $parameters = [];

    /**
     * Url components generated by parse_url()
     *
     * Notice: when generate url, the query component is not used.
     *
     * Keys:
     *  - scheme
     *  - host
     *  - port
     *  - user
     *  - pass
     *  - path
     *  - query(parameters)
     *  - fragment(#)
     *
     * @type    string[]
     */
    protected $urlComponents = [];


    /**
     * Constructor
     */
    public function __construct()
    {
        $this->initialize();
    }


    /**
     * @see UrlGeneratorInterface::getFullLink()
     *
     * @param   string  $title
     * @param   string  $rawHtml    Extra html in <a> tag
     * @return  string
     */
    public function getFullLink($title, $rawHtml = '')
    {
        if (!empty($rawHtml)) {
            $rawHtml = ' ' . $rawHtml;
        }

        return "<a href='" . $this->getFullUrl() . "'" . $rawHtml . ">" .
            $title . "</a>";
    }


    /**
     * @see UrlGeneratorInterface::getFullUrl()
     *
     * http_build_url() maybe not usable.
     *
     * @return  string
     */
    public function getFullUrl()
    {
        $components = $this->urlComponents;
        $arrayUtil = UtilContainer::getInstance()->getArray();
        $url = '';

        $url .= array_key_exists('scheme', $components)
            ? $components['scheme'] . '://' : '//';

        $url .= array_key_exists('user', $components)
            ? $components['user'] .
                (array_key_exists('pass', $components)
                    ? ':' . $components['pass']
                    : '')
                . '@'
            : '';

        $url .= $arrayUtil->getIdx($components, 'host', '');

        $url .= $arrayUtil->getIdx($components, 'path', '/');

        if (!empty($this->parameters)) {
            $url .= '?' . http_build_query($this->parameters);
        }

        if (array_key_exists('fragment', $components)) {
            $url .= '#' . $components['fragment'];
        }

        return $url;
    }


    /**
     * @see UrlGeneratorInterface::getLink()
     *
     * @param   string  $title
     * @param   string  $rawHtml    Extra html in <a> tag
     * @return  string
     */
    public function getLink($title, $rawHtml = '')
    {
        if (!empty($rawHtml)) {
            $rawHtml = ' ' . $rawHtml;
        }

        return "<a href='" . $this->getUrl() . "'" . $rawHtml . ">" .
            $title . "</a>";
    }


    /**
     * @see UrlGeneratorInterface::getUrl()
     *
     * @return  string
     */
    public function getUrl()
    {
        if (empty($this->parameters)) {
            return '';
        }

        return '?' . http_build_query($this->parameters);
    }


    /**
     * @return  static
     */
    protected function initialize()
    {
        $httpUtil = UtilContainer::getInstance()->getHttp();

        $this->urlComponents = parse_url($httpUtil->getSelfUrl(true));

        $this->parameters = $httpUtil->getGets();

        return $this;
    }


    /**
     * @param   string  $queryString
     * @return  string[]
     */
    protected function parseQueryParameters($queryString)
    {
        $parameters = [];

        parse_str($queryString, $parameters);

        return $parameters;
    }


    /**
     * Reset stored information
     *
     * @return  static
     */
    protected function reset()
    {
        $this->parameters = [];

        $this->urlComponents = [];

        return $this;
    }


    /**
     * @see UrlGeneratorInterface::setBaseUrl()
     *
     * @param   string  $url
     * @return  static
     */
    public function setBaseUrl($url)
    {
        $components = parse_url($url);

        unset($components['query']);
        unset($components['fragment']);

        $this->urlComponents = array_merge($this->urlComponents, $components);

        return $this;
    }


    /**
     * @see UrlGeneratorInterface::setFullUrl()
     *
     * @param   string  $url
     * @return  static
     */
    public function setFullUrl($url)
    {
        $this->reset();

        $components = parse_url($url);

        if (array_key_exists('query', $components)) {
            $this->parameters = $this->parseQueryParameters(
                $components['query']
            );
        }

        $this->urlComponents = $components;

        return $this;
    }


    /**
     * @see UrlGeneratorInterface::setParameter()
     *
     * @param   string  $name
     * @param   string  $value
     * @return  static
     */
    public function setParameter($name, $value)
    {
        $this->parameters[$name] = $value;

        return $this;
    }


    /**
     * @see UrlGeneratorInterface::setParameters()
     *
     * @param   array   $parameters Array k-v is parameter name & value
     * @return  static
     */
    public function setParameters(array $parameters)
    {
        $this->parameters = array_merge($this->parameters, $parameters);

        return $this;
    }


    /**
     * @see UrlGeneratorInterface::unsetAllParameters()
     *
     * @return  static
     */
    public function unsetAllParameters()
    {
        $this->parameters = [];

        return $this;
    }


    /**
     * @see UrlGeneratorInterface::unsetParameter()
     *
     * @param   string  $name
     * @return  static
     */
    public function unsetParameter($name)
    {
        unset($this->parameters[$name]);

        return $this;
    }


    /**
     * @see UrlGeneratorInterface::unsetParameters()
     *
     * @param   string[]    $names
     * @return  static
     */
    public function unsetParameters(array $names)
    {
        $this->parameters = array_diff_key(
            $this->parameters,
            array_fill_keys($names, null)
        );

        return $this;
    }
}
